// Minimal declaration for jsonwebtoken to fix missing types issue// Place this file in: server/types/jsonwebtoken.d.tsdeclare module "jsonwebtoken" {  export interface JwtPayload {    [key: string]: any;    sub?: string;    email?: string;    name?: string;    role?: string;    iat?: number;    exp?: number;  }  export interface SignOptions {    algorithm?: string;    expiresIn?: string | number;    notBefore?: string | number;    audience?: string | string[];    issuer?: string;    jwtid?: string;    subject?: string;    noTimestamp?: boolean;    header?: object;    keyid?: string;    mutatePayload?: boolean;  }  export interface VerifyOptions {    algorithms?: string[];    audience?: string | RegExp | Array<string | RegExp>;    clockTolerance?: number;    issuer?: string | string[];    ignoreExpiration?: boolean;    ignoreNotBefore?: boolean;    jwtid?: string;    subject?: string;    maxAge?: string | number;  }  export type Secret = string | Buffer;  export function sign(    payload: object | string | Buffer,    secretOrPrivateKey: Secret,    options?: SignOptions  ): string;  export function verify(    token: string,    secretOrPublicKey: Secret,    options?: VerifyOptions  ): string | JwtPayload;  export function decode(    token: string,    options?: { json?: boolean; complete?: boolean }  ): null | { [key: string]: any };}
